mapper.insert:

insert into recommend (recommend_id, product_id, is_visable, 
  is_first, online_subscription, description, 
  ordered, recommend_degree, admin
  )
values (#{recommendId,jdbcType=INTEGER}, #{productId,jdbcType=INTEGER}, #{isVisable,jdbcType=INTEGER}, 
  #{isFirst,jdbcType=INTEGER}, #{onlineSubscription,jdbcType=INTEGER}, #{description,jdbcType=VARCHAR}, 
  #{ordered,jdbcType=INTEGER}, #{recommendDegree,jdbcType=INTEGER}, #{admin,jdbcType=INTEGER}
  )

mapper.select:
select r.*,bp.product_name from recommend r LEFT JOIN bk_product bp on r.product_id =bp.product_id
bp.product_name like concat('%',#{productName},'%')
order by r.ordered limit #{pageInfo.startIndex},#{pageInfo.pageSize}
